mapper.select:
SELECT p.payment_id AS paymentId, p.payment_method AS paymentMethod, m.menu_name AS menuName, p.total_price AS totalPrice, p.payment_date AS paymentDate, b.branch_name AS branchName, p.serial_number AS serialNumber FROM payment p JOIN menu m ON p.menu_id = m.menu_id JOIN branch b ON p.branch_id = b.branch_id
p.branch_id IN
#{id}
AND p.payment_date BETWEEN #{from, jdbcType=TIMESTAMP} AND #{to, jdbcType=TIMESTAMP}
ORDER BY p.payment_date DESC
